import React, { useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from './textfield';


const useStyles = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),
      width: '15ch',
    },
  },
}));

export default function MultilineTextFields(playerData) {
  const classes = useStyles();
  const [currencies, setCurrencies] = React.useState([])
  const [isLoading, setIsLoading] = React.useState(false);
  const [pokemonsInUse, setPokemonsInUse] = React.useState([]);

  useEffect(() => {
    setIsLoading(true);

    let currenciesData = []
    playerData.playerData.pokemons.forEach((pokemon) => {

      if (pokemon.number !== 0) {
        for (let index = 0; index < pokemon.number; index++) {

          const isUsed = pokemonsInUse.find(isUsed => isUsed.name === pokemon.name)
          console.log(isUsed)
          if (!isUsed) {
            currenciesData.push(pokemon)
          }
        }
      }
    })
    setCurrencies(currenciesData)
    setIsLoading(false);
  }, []);

  const addNewPokemonInUse = (newPokemon) =>{
    setPokemonsInUse.push(newPokemon)
  }

  return (

    <form className={classes.root} noValidate autoComplete="off">
      {isLoading ? null :
        <div>
          <TextField pokemons={currencies} />
          <TextField pokemons={currencies} />
          <TextField pokemons={currencies} />
          <TextField pokemons={currencies} />
          <TextField pokemons={currencies} />
          <TextField pokemons={currencies} />
        </div>}

    </form>
  )
}
