import React, { useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextFieldComponent from './TextFieldComponent';


const useStyles = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),
      width: '15ch',
    },
  },
}));

const MultilineTextFields = (playerData) => {
  const classes = useStyles();
  const [pokemon, setPokemon] = React.useState([])
  const [isLoading, setIsLoading] = React.useState(true);
  const [pokemonsInUse, setPokemonsInUse] = React.useState([]);



  useEffect(() => {
    setIsLoading(true);
    let pokemonData = []
    playerData.playerData.pokemons.forEach((pokemon) => {
      if (pokemon.number !== 0) {
        for (let index = 0; index < pokemon.number; index++) {
          pokemon.inUse = false;
          console.log(pokemon)
          pokemonData.push(pokemon)
        }
      }
    })
    setPokemon(pokemonData)
    setIsLoading(false)
  }, []);




  const addNewPokemonInUse = (newPokemon) => {
    let pokesInUse = pokemonsInUse
    pokesInUse.push(newPokemon)
    setPokemonsInUse(pokesInUse)
  }

  return (

    <form className={classes.root} noValidate autoComplete="off">

      {isLoading ? null :
        <div>
          <TextFieldComponent pokemons={pokemon} onUsePokemon={addNewPokemonInUse} pokemonsInUse={pokemonsInUse} />
          <TextFieldComponent pokemons={pokemon} onUsePokemon={addNewPokemonInUse} pokemonsInUse={pokemonsInUse} />
          <TextFieldComponent pokemons={pokemon} onUsePokemon={addNewPokemonInUse} pokemonsInUse={pokemonsInUse} />
          <TextFieldComponent pokemons={pokemon} onUsePokemon={addNewPokemonInUse} pokemonsInUse={pokemonsInUse} />
          <TextFieldComponent pokemons={pokemon} onUsePokemon={addNewPokemonInUse} pokemonsInUse={pokemonsInUse} />
          <TextFieldComponent pokemons={pokemon} onUsePokemon={addNewPokemonInUse} pokemonsInUse={pokemonsInUse} />
        </div>}

    </form>
  )
}
export default MultilineTextFields;
