import React, { useEffect } from 'react';
import TextField from '@material-ui/core/TextField';
import MenuItem from '@material-ui/core/MenuItem';

const TextFieldComponent = props => {
    const [currency, setCurrency] = React.useState('')
    const [pokemonsAvaiable, setPokemonsAvaiable] = React.useState([props.pokemons])
    console.log(props)
    const handleChange = (event) => {
        setCurrency(event.target.value);
        props.onUsePokemon(event.target.value)
        let pokeNotUse = []
        props.pokemons.forEach((pokemon, index) => {
            const isUsed = props.pokemonsInUse.find(poke => poke === index)
            if (!isUsed) {
                pokeNotUse.push(pokemon)
            }
        })
        setPokemonsAvaiable(pokeNotUse)
    };

    useEffect(() => {
        console.log("oi")
        let pokeNotUse = []
        props.pokemons.forEach((pokemon, index) => {
            const isUsed = props.pokemonsInUse.find(poke => poke === index)
            if (!isUsed) {
                pokeNotUse.push(pokemon)
            }
        })
        setPokemonsAvaiable(pokeNotUse)
    }, []);


    return (

        <TextField
            id="standard-select-currency"
            select
            label="Select"
            value={currency}
            onChange={handleChange}
            helperText="Please select your currency"
        >
            {
                pokemonsAvaiable.map((pokemon, index) => (
                    pokemon.number !== 0 ?
                        <MenuItem key={index} value={index}>
                            {pokemon.name}
                        </MenuItem> : null
                ))
            }
        </TextField >

    )
}

export default TextFieldComponent;
