import logo from './logo.svg';
import './App.css';
import TextSelect from './components/textselect'
import Button from '@material-ui/core/Button';

function App() {
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState();
  const [loadedPlayer, setLoadedPlayer] = useState([]);


  useEffect(() => {
    const sendRequest = async () => {
      setIsLoading(true);
      try {
        const response = await fetch('http://localhost:5000/api/player/');

        const responseData = await response.json();

        if (!response.ok) {
          throw new Error(responseData.message);
        }
        setLoadedPlayer(responseData.players);
      } catch (err) {
        setError(err.message);
      }
      setIsLoading(false);
    };
    sendRequest();
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <React.Fragment>
          <ErrorModal error={error} onClear={errorHandler} />
          {isLoading && (
            <div className="center">
              <LoadingSpinner />
            </div>
          )}{!isLoading && loadedPlayer &&
            <div>
              {
                loadedPlayer.map((player, index) => (
                  <div className="Title">{player.name}
                    <TextSelect playerData={player} />
                  </div>
                ))
              }
              <Button variant="contained">Change</Button>
            </div>
          }
        </React.Fragment>
      </header>
    </div>
  );
}

export default App;
