import React, { useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextFieldComponent from './TextFieldComponent';


const useStyles = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),
      width: '15ch',
    },
  },
}));

const MultilineTextFields = (playerData) => {
  const classes = useStyles();
  const [pokemons, setPokemons] = React.useState([])
  const [isLoading, setIsLoading] = React.useState(true);
  const arrLength = 6;
  const [elRefs, setElRefs] = React.useState([]);


  useEffect(() => {
    setIsLoading(true);
    let pokemonData = []
    playerData.playerData.pokemons.forEach((pokemon) => {
      if (pokemon.number !== 0) {
        for (let index = 0; index < pokemon.number; index++) {
          pokemon.inUse = false;
          pokemonData.push(pokemon)
        }
      }
    })
    setPokemons(pokemonData)
    setIsLoading(false)
    setElRefs(elRefs => (
      Array(arrLength).fill().map((_, i) => elRefs[i] || React.createRef())
    ));
  }, [arrLength]);




  const addNewPokemonInUse = (usedPokemon) => {
    let pokemon = pokemons[usedPokemon];
    if (pokemon.number === 1) {
      pokemon.inUse = true;
      pokemons[usedPokemon] = pokemon
      setPokemons((prevPokemons) => { return prevPokemons.concat(pokemon) })
    } else {
      pokemon.number -= 1
      console.log(pokemon.number)
      pokemons[usedPokemon] = pokemon
      setPokemons((prevPokemons) => { return prevPokemons.concat(pokemon) })
    }
  }


  return (

    <form className={classes.root} noValidate autoComplete="off">

      {isLoading ? null :
        <div>
          <TextFieldComponent ref={elRefs[0]} pokemons={pokemons} onUsePokemon={addNewPokemonInUse} />
          <TextFieldComponent ref={elRefs[1]} pokemons={pokemons} onUsePokemon={addNewPokemonInUse} />
          <TextFieldComponent ref={elRefs[2]} pokemons={pokemons} onUsePokemon={addNewPokemonInUse} />
          <TextFieldComponent ref={elRefs[3]} pokemons={pokemons} onUsePokemon={addNewPokemonInUse} />
          <TextFieldComponent ref={elRefs[4]} pokemons={pokemons} onUsePokemon={addNewPokemonInUse} />
          <TextFieldComponent ref={elRefs[5]} pokemons={pokemons} onUsePokemon={addNewPokemonInUse} />
        </div>}

    </form>
  )
}
export default MultilineTextFields;
