import React, { useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextFieldComponent from './TextFieldComponent';


const useStyles = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),
      width: '15ch',
    },
  },
}));

const MultilineTextFields = (playerData) => {
  const classes = useStyles();
  const [pokemons, setPokemons] = React.useState([])
  const [isLoading, setIsLoading] = React.useState(true)
  const [isUpdate, setIsUpdate] = React.useState(false)



  useEffect(() => {
    setIsLoading(true);
    let pokemonData = []
    playerData.playerData.pokemons.forEach((pokemon) => {
      if (pokemon.number !== 0) {
        for (let index = 0; index < pokemon.number; index++) {
          pokemon.inUse = false;
          pokemonData.push(pokemon)
        }
      }
    })
    setPokemons(pokemonData)
    setIsLoading(false)
    setIsUpdate(true)
  }, []);




  const addNewPokemonInUse = (usedPokemon) => {
    setIsUpdate(false)
    let pokemonsAvaiable = pokemons;
    let pokemon = pokemons[usedPokemon];
    if (pokemon.number === 1) {
      pokemon.inUse = true;
      pokemonsAvaiable[usedPokemon] = pokemon
    } else {
      pokemon.number -= 1
    }
    setPokemons(pokemonsAvaiable)
    setIsUpdate(true)
  }


  return (

    <form className={classes.root} noValidate autoComplete="off">

      {isLoading && !isUpdate ? null :
        <div>
          <TextFieldComponent pokemons={pokemons} onUsePokemon={addNewPokemonInUse} />
          <TextFieldComponent pokemons={pokemons} onUsePokemon={addNewPokemonInUse} />
          <TextFieldComponent pokemons={pokemons} onUsePokemon={addNewPokemonInUse} />
