import './App.css';
import MultilineTextFields from './components/MultilineTextFields'
import Button from '@material-ui/core/Button';
import React, { useEffect, useState } from 'react';
import ErrorModal from './components/errorModal';
import LoadingSpinner from './components/loadingSpinner';

function App() {
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState();
  const [loadedPlayer, setLoadedPlayer] = useState([]);
  const [selectPlayerPokemons, setselectPlayerPokemons]  = useState([]);


  useEffect(() => {
    const sendRequest = async () => {
      setIsLoading(true);
      try {
        const response = await fetch('http://localhost:5000/api/player/');

        const responseData = await response.json();

        if (!response.ok) {
          throw new Error(responseData.message);
        }
        setLoadedPlayer(responseData.players);
      } catch (err) {
        setError(err.message);
      }
      setIsLoading(false);
    };
    sendRequest();
  }, []);

  const errorHandler = () => {
    setError(null);
  };

  const updateSelectPlayerPokemon = (pokemonsUsed) => {
    console.log(pokemonsUsed)
  }

  return (
    <div className="App">
      <header className="App-header">
        <React.Fragment>
          <ErrorModal error={error} onClear={errorHandler} />
          {isLoading && (
            <div className="center">
              <LoadingSpinner />
            </div>
          )}{!isLoading && loadedPlayer &&
            <div>
              {
                loadedPlayer.map((player, index) => (
                  <div className="Title">{player.name}
                    <MultilineTextFields playerData={player} playerPokemons={updateSelectPlayerPokemon}/>
                  </div>
                ))
              }
              <Button variant="contained">Change</Button>
            </div>
          }
        </React.Fragment>
      </header>
    </div>
  );
}

export default App;
