import React, { useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextFieldComponent from './TextFieldComponent';


const useStyles = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),
      width: '15ch',
    },
  },
}));

const MultilineTextFields = (playerData) => {
  const classes = useStyles();
  const [currencies, setCurrencies] = React.useState([])
  const [isLoading, setIsLoading] = React.useState(true);
  const [pokemonsInUse, setPokemonsInUse] = React.useState([]);

  const addNewPokemonInUse = (newPokemon) => {
    setPokemonsInUse(prevPokemonsInUse => prevPokemonsInUse.concat(newPokemon))
  }

  useEffect(() => {
    const loadPokemons = async () => {

      setIsLoading(true);

      let currenciesData = []
      await playerData.playerData.pokemons.forEach((pokemon) => {
        if (pokemon.number !== 0) {
          for (let index = 0; index < pokemon.number; index++) {

            const isUsed = pokemonsInUse.find(isUsed => isUsed.name === pokemon.name)
            console.log(isUsed)
            if (!isUsed) {
              currenciesData.push(pokemon)
            }
          }
        }
      })
      setCurrencies(currenciesData)
      setIsLoading(false);
    }
    loadPokemons();

  }, [playerData, pokemonsInUse]);



  return (

    <form className={classes.root} noValidate autoComplete="off">

      {isLoading ? null :
        <div>
          <TextFieldComponent pokemons={currencies} onUsePokemon={addNewPokemonInUse} />
          <TextFieldComponent pokemons={currencies} onUsePokemon={addNewPokemonInUse} />
          <TextFieldComponent pokemons={currencies} onUsePokemon={addNewPokemonInUse} />
          <TextFieldComponent pokemons={currencies} onUsePokemon={addNewPokemonInUse} />
          <TextFieldComponent pokemons={currencies} onUsePokemon={addNewPokemonInUse} />
          <TextFieldComponent pokemons={currencies} onUsePokemon={addNewPokemonInUse} />
        </div>}

    </form>
  )
}
