import React, { useEffect, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from './textfield';
import ErrorModal from './errorModal';
import LoadingSpinner from './loadingSpinner';

const useStyles = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),
      width: '15ch',
    },
  },
}));

export default function MultilineTextFields() {
  const classes = useStyles();
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState();
  const [loadedPlayer, setLoadedPlayer] = useState();


  useEffect(() => {
    const sendRequest = async () => {
      setIsLoading(true);
      try {
        const response = await fetch('http://localhost:5000/api/player/');

        const responseData = await response.json();

        if (!response.ok) {
          throw new Error(responseData.message);
        }
        setLoadedPlayer(responseData.players);
      } catch (err) {
        setError(err.message);
      }
      setIsLoading(false);
    };
    sendRequest();
  }, []);



  /*const handleChange = (event) => {
    setCurrency(event.target.value);
  };*/


  const errorHandler = () => {
    setError(null);
  };

  return (
    <React.Fragment>
      <ErrorModal error={error} onClear={errorHandler} />
      {isLoading && (
        <div className="center">
          <LoadingSpinner />
        </div>
      )}{!isLoading && loadedPlayer &&
        <form className={classes.root} noValidate autoComplete="off">
          <div>
            <TextField currencies={loadedPlayer[0].pokemons} />
            <TextField currencies={loadedPlayer[0].pokemons} />
            <TextField currencies={loadedPlayer[0].pokemons} />
            <TextField currencies={loadedPlayer[0].pokemons} />
            <TextField currencies={loadedPlayer[0].pokemons} />
            <TextField currencies={loadedPlayer[0].pokemons} />
          </div>

        </form>
      }
    </React.Fragment>
  );
}
